<?phpfunction fizzbuzz($fizz,$buzz,$max=100){    $output=array();    for ($i=1; $i <= $max; $i++){        $output[$i]='';                if($i % $fizz == 0 && $i % $buzz == 0){        $output[$i]="FizzBuzz <br>";            }else if($i % $fizz == 0){        $output[$i]="Fizz <br>";            } if($i % $buzz == 0){        $output[$i].="Buzz <br>";            }else if(empty($output[$i])){            $output[$i]=$i . "<br>";        }    }    return $output;}foreach(fizzbuzz(3,5) as $out){    echo $out;}?>