<?phpfor ($i = 1; $i <= 100; $i++){   if($i / 3 == round($i / 3) && $i / 5 == round($i / 5)){      echo $i . "FizzBuzz<br />";   }   else if($i / 3 == round($i / 3)){      echo $i . "Fizz<br />";   }   else if($i / 5 == round($i / 5)){      echo $i . "Buzz<br />";   }   else {      echo $i."<br />";   }}?>